DROP TABLE IF EXISTS `subscriptions`;

CREATE TABLE `subscriptions` (
  `id` bigint(20) unsigned NOT NULL AUTO_INCREMENT,
  `profile_id` varchar(30) DEFAULT NULL,
  `provider` varchar(10) NOT NULL,
  `product_id` varchar(64) NOT NULL,
  `expiry` datetime NOT NULL,
  `username` varchar(255) DEFAULT NULL,
  `developer` varchar(100) DEFAULT NULL,
  `version` bigint(20) DEFAULT NULL,
  `created` datetime DEFAULT NULL,
  `updated` datetime DEFAULT NULL,
  `provider_information` varchar(377) DEFAULT NULL,
  `activated` bit(1) DEFAULT b'0',
  PRIMARY KEY (`id`),
  UNIQUE KEY `id_UNIQUE` (`id`),
  KEY `expiry` (`expiry`),
  KEY `provider` (`provider`),
  KEY `profile_expiry` (`profile_id`,`expiry`),
  KEY `provider_info_indx` (`provider_information`),
  KEY `profile_id` (`profile_id`)
) ENGINE=InnoDB AUTO_INCREMENT=142 DEFAULT CHARSET=latin1 COMMENT='New subscriptions table for use with OAUTH version of sstv.';


DROP TABLE IF EXISTS `config_stream`;
CREATE TABLE `config_stream` (
  `id` varchar(255) NOT NULL,
  `data` mediumtext,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

